class Point:
    decor = '&lt;&gt;'

    def __init__(self, x, y):
        self.x = x
        self.y = y

    def move(self, dx, dy):
        self.x += dx
        self.y += dy

    def __repr__(self):
        if Point.decor == '&lt;&gt;':
            return f"&lt;{self.x}, {self.y}&gt;"
        elif Point.decor == '[]':
            return f"[{self.x}, {self.y}]"
        else:
            return f"({self.x}, {self.y})"

